    //==============================================
    this.contador2 = this.date.getSeconds() *10;




    }

    start() {

        if(this.contador == 0){
          this.contador++;

          this.timer = setInterval(() => {
            this.updateTimer();
          }, 1000);
        }     

    }

    stop() {    

      this.animate = false;
      clearInterval(this.timer);
      this.contador=0;
    }

    reset() {
      this.tokensRNC=  this.tokensRNC + this.contador2;
      this.contador2 = 0;
      this.hours = 0;
      this.minutes = 0;
      this.seconds = 0;
      this.stop();
      this.contador=0;
    }




  }

